function calculaIMC(){document.querySelector("button").addEventListener("click",e=>{e.preventDefault();var e=document.querySelector("#input-peso"),n=document.querySelector("#input-altura"),a=document.querySelector(".resultado"),u=e.value/(n.value*n.value);u<=18.5?(a.innerHTML=`<span>O seu imc é ${u.toFixed(2)} (Abaixo do peso)</span>`,a.style.background="#f1c40f"):18.5<=u&&u<=24.9?(a.innerHTML=`<span>O seu imc é ${u.toFixed(2)} (Peso Normal)</span>`,a.style.background="#2ecc71"):25.9<=u&&u<=29.9?(a.innerHTML=`<span>O seu imc é ${u.toFixed(2)} (Sobrepeso)</span>`,a.style.background="#e67e22"):30<=u&&u<=34.9?(a.innerHTML=`<span>O seu imc é ${u.toFixed(2)} (Obesidade grau 1)</span>`,a.style.background="#f39c12"):35<=u&&u<=39.9?(a.innerHTML=`<span>O seu imc é ${u.toFixed(2)} (Obesidade grau 2)</span>`,a.style.background="#e74c3c"):(a.innerHTML=`<span>O seu imc é ${u.toFixed(2)} (Obesidade grau 3)</span>`,a.style.background="#c0392b"),e.value="",n.value=""})}calculaIMC();